// Build hierarchical structure from flat list of folders
// Convert Google Drive folder structure to JSON matching the desired structure format

const rootFolderId = $('Set Configuration').first().json.rootFolderId;
const allFolders = $json.files || [];

console.log('Total folders found in Google Drive:', allFolders.length);

// Build a map: parentId -> [children]
const childrenMap = new Map();
for (const folder of allFolders) {
  const parentId = folder.parents && folder.parents.length > 0 ? folder.parents[0] : rootFolderId;
  if (!childrenMap.has(parentId)) {
    childrenMap.set(parentId, []);
  }
  childrenMap.get(parentId).push({
    id: folder.id,
    name: folder.name
  });
}

console.log('Folders directly under root:', childrenMap.get(rootFolderId)?.length || 0);

// Build tree structure starting directly from rootFolderId
// Function to recursively build tree structure
function buildTree(parentId) {
  const children = childrenMap.get(parentId) || [];
  return children.map(child => {
    const node = { name: child.name };
    const childChildren = buildTree(child.id);
    if (childChildren.length > 0) {
      node.children = childChildren;
    }
    return node;
  });
}

// Build tree starting from rootFolderId (which is now a folder, not a drive root)
const actualStructure = buildTree(rootFolderId);

console.log('Actual structure built:', JSON.stringify(actualStructure, null, 2));

return [{ json: { actualStructure, allFolders } }];

// Compare desired structure with actual structure
// Include ALL folders with exists: true/false field

const desiredStructure = typeof $('Set Configuration').first().json.desiredStructure === 'string'
  ? eval($('Set Configuration').first().json.desiredStructure)
  : $('Set Configuration').first().json.desiredStructure;

const actualStructure = $json.actualStructure || [];

console.log('Desired structure:', JSON.stringify(desiredStructure, null, 2));
console.log('Actual structure:', JSON.stringify(actualStructure, null, 2));

// Build a lookup map: parentName -> Map<childName, exists>
// This allows us to check if a folder exists by its name and parent context
function buildNameMap(structure, parentName = null) {
  const nameMap = new Map(); // parentName -> Set<childNames>

  function processNode(node, parent) {
    const parentKey = parent || 'ROOT';
    if (!nameMap.has(parentKey)) {
      nameMap.set(parentKey, new Set());
    }
    nameMap.get(parentKey).add(node.name);

    if (node.children && node.children.length > 0) {
      for (const child of node.children) {
        processNode(child, node.name);
      }
    }
  }

  for (const node of structure) {
    processNode(node, null);
  }

  return nameMap;
}

const actualNameMap = buildNameMap(actualStructure);

console.log('Actual name map:', JSON.stringify(
  Array.from(actualNameMap.entries()).map(([k, v]) => [k, Array.from(v)]),
  null, 2
));

// Function to check if a folder exists by name and parent
function folderExists(folderName, parentName = null) {
  const parentKey = parentName || 'ROOT';
  const exists = actualNameMap.has(parentKey) && actualNameMap.get(parentKey).has(folderName);

  // Debug: log what we're checking
  if (!exists) {
    console.log(`  Folder NOT found: name="${folderName}", parent="${parentKey}"`);
    if (actualNameMap.has(parentKey)) {
      console.log(`  Available children in ${parentKey}:`, Array.from(actualNameMap.get(parentKey)));
    } else {
      console.log(`  Parent ${parentKey} not found in actual structure`);
    }
  } else {
    console.log(`  ✓ Folder found: name="${folderName}", parent="${parentKey}"`);
  }

  return exists;
}

// Function to recursively check folders and add exists field
function checkFolders(desiredNode, parentName = null) {
  const exists = folderExists(desiredNode.name, parentName);

  console.log(`Checking folder: ${desiredNode.name}, parent: ${parentName || 'ROOT'}, exists: ${exists}`);

  const result = {
    name: desiredNode.name,
    exists: exists
  };

  // Check children - always include all children
  if (desiredNode.children && desiredNode.children.length > 0) {
    result.children = desiredNode.children.map(child => 
      checkFolders(child, desiredNode.name)
    );
  }

  return result;
}

// Check all folders
const folderStatus = desiredStructure.map(node => checkFolders(node));

console.log('Folder status:', JSON.stringify(folderStatus, null, 2));

return [{ json: { folders: folderStatus } }];

// Flatten structure and produce a list of folders to create for THIS run.
// - Only output folders where `exists === false` AND the parent already exists.
// - Compute parentFolderId from the current Drive state (Build Actual Structure).
//
// On first run: only top-level folders are created.
// On second run: their children are created.
// On third run: grandchildren, etc.
//
// You keep running the workflow until it logs "0 folders to create".

// --- CONFIG & INPUT ---

const rootFolderId = $('Set Configuration').first().json.rootFolderId;
const mainFolderId = rootFolderId;

console.log('Using rootFolderId as mainFolderId:', mainFolderId);

// This comes from "Set Missing Folders"
const folderStatusTree = $json.folders || [];

// From "Build Actual Structure" (current Drive state)
const actualStructure = $('Build Actual Structure').first().json.actualStructure || [];
const allFolders = $('Build Actual Structure').first().json.allFolders || [];

console.log('Actual structure nodes:', actualStructure.length);
console.log('Total folders in allFolders:', allFolders.length);

// --- BUILD path -> folderId MAP FROM CURRENT DRIVE STATE ---

// We'll reconstruct full paths like "00 – Governance/Charter"
const pathToId = new Map();

function buildPathMap(structure, parentPath = '', parentId = mainFolderId) {
  for (const node of structure) {
    const currentPath = parentPath ? `${parentPath}/${node.name}` : node.name;

    // Find the matching folder from allFolders by name + parentId
    const folder = allFolders.find(f => {
      const fParentId = f.parents && f.parents.length > 0 ? f.parents[0] : rootFolderId;
      return f.name === node.name && fParentId === parentId;
    });

    if (folder) {
      pathToId.set(currentPath, folder.id);
      // Recurse into children
      if (node.children && node.children.length > 0) {
        buildPathMap(node.children, currentPath, folder.id);
      }
    }
  }
}

buildPathMap(actualStructure);

console.log('Existing paths in Drive:');
for (const [p, id] of pathToId.entries()) {
  console.log(`  ${p} -> ${id}`);
}

// --- FLATTEN DESIRED STRUCTURE (with exists flags) ---
// We only output folders where:
//   - node.exists === false
//   - the parent "exists" right now (either root or in pathToId / actualStructure)

function flattenForCreation(structure, parentPath = '', parentName = null, depth = 0, parentExists = true) {
  const result = [];

  for (const node of structure) {
    const currentPath = parentPath ? `${parentPath}/${node.name}` : node.name;
    const thisExists = node.exists === true;

    // Decide if THIS node should be created in this run
    // Only when it doesn't exist yet, and its parent exists.
    if (!thisExists && parentExists) {
      let parentFolderId;

      if (!parentPath) {
        // Top-level folder under the main/root folder
        parentFolderId = mainFolderId;
      } else {
        // Parent is some subfolder; look it up by its full path
        parentFolderId = pathToId.get(parentPath) || mainFolderId;
      }

      console.log(
        `Scheduling create: "${currentPath}" ` +
        `(depth ${depth}, parentPath="${parentPath}", parentFolderId=${parentFolderId})`
      );

      result.push({
        name: node.name,
        path: currentPath,
        parentPath,
        parentName,
        depth,
        parentFolderId,
      });
    } else {
      console.log(
        `Skipping for now: "${currentPath}" (exists=${thisExists}, parentExists=${parentExists})`
      );
    }

    // Children: their parent "exists" if:
    //  - this node already exists in Drive (thisExists === true), OR
    //  - we have a path->id entry for it (i.e., it existed before this run).
    const childParentExists = thisExists || pathToId.has(currentPath);

    if (node.children && node.children.length > 0) {
      const childResults = flattenForCreation(
        node.children,
        currentPath,
        node.name,
        depth + 1,
        childParentExists,
      );
      result.push(...childResults);
    }
  }

  return result;
}

const flattened = flattenForCreation(folderStatusTree);

// Sort by depth so parents of this batch are before children of this batch
flattened.sort((a, b) => a.depth - b.depth);

console.log(`\nFound ${flattened.length} folders to create in THIS run:`);
flattened.forEach(f =>
  console.log(
    `  [depth ${f.depth}] ${f.path} (parentPath="${f.parentPath}", parentFolderId=${f.parentFolderId})`,
  ),
);

// Output each folder as a separate item
const outputItems = flattened.map(folder => ({
  json: {
    ...folder,
    mainFolderId,
    rootFolderId,
  },
}));

console.log(`\n=== OUTPUT: Returning ${outputItems.length} items ===`);

return outputItems;
